SELECT contest_id, hacker_id, name, a,b,c,d FROM (SELECT contest_id, hacker_id, name, SUM(total_submissions) a, SUM(total_accepted_submissions) b FROM Contests left join Colleges USING(contest_id) left join Challenges USING(college_id) left join Submission_Stats USING(challenge_id) GROUP BY contest_id, hacker_id, name) A left join (SELECT contest_id, hacker_id, name, SUM(total_views) c, SUM(total_unique_views ) d FROM Contests left join Colleges USING(contest_id) left join Challenges USING(college_id) left join View_Stats USING(challenge_id) GROUP BY contest_id, hacker_id, name) B USING(contest_id, hacker_id, name) WHERE a OR b OR c OR d;