SELECT H.hacker_id, H.name, COUNT(C.challenge_id) AS total_count FROM Hackers H join Challenges C
ON H.hacker_id = C.hacker_id GROUP BY H.hacker_id, H.name HAVING total_count = (SELECT COUNT(temp1.challenge_id) as max_count FROM challenges temp1 GROUP BY temp1.hacker_id ORDER BY max_count DESC limit 1) OR total_count IN (SELECT DISTINCT other_counts FROM (SELECT H2.hacker_id, H2.name, COUNT(C2.challenge_id) AS other_counts FROM Hackers H2 join Challenges C2 ON H2.hacker_id = C2.hacker_id GROUP BY H2.hacker_id, H2.name) temp2 GROUP BY other_counts HAVING COUNT(other_counts) =1) ORDER BY total_count DESC, H.hacker_id